CREATE ALGORITHM=UNDEFINED DEFINER=`sa`@`localhost` SQL SECURITY DEFINER VIEW `vw_resumo_vendas` AS select `u`.`nome` AS `vendedor`,`l`.`nome` AS `loja`,date_format(`p`.`data_hora_registro`,'%d/%m/%Y') AS `data_venda`,date_format(`p`.`data_hora_registro`,'%H:%i') AS `hora_venda`,(select count(0) from `carro_pedido` where (`carro_pedido`.`id_pedido` = `p`.`id`)) AS `quantidade_carros_vendidos`,concat('R$ ',replace(replace(replace(format(`p`.`total`,2),'.','|'),',','.'),'|',',')) AS `valor_total_venda` from ((((`pedido` `p` join `cliente` `c` on((`p`.`id_cliente` = `c`.`id`))) join `usuario` `u` on((`p`.`id_usuario` = `u`.`id`))) join `loja` `l` on((`p`.`id_loja` = `l`.`id`))) join `carro_pedido` `cp` on((`cp`.`id_pedido` = `p`.`id`)))